import React from "react";
import { Card , Button} from 'react-bootstrap'; 

const DEFAULT_BOOK_IMAGE =
  "./assets/images/bookDefault.png";


const BookDetail = ({ book }) => {
  let item = book.volumeInfo;

  // Author
  let author = item.authors;

  // Title
  let title = item.title;

  // Description
  let desc = item.description;

  if (typeof desc === 'undefined') {
    desc = 'No description available';
  }

  if (typeof author === 'undefined') {
    author = 'No author';
  }
  
  
  return (
    
    <div className="card mb-2">
            <Card>  

                <Card.Img className="card-img-top img-fluid" src={book.volumeInfo.imageLinks === undefined ? DEFAULT_BOOK_IMAGE : book.volumeInfo.imageLinks.thumbnail} alt={book.title} />  
                <Card.Body className="card-body"> 
                    <Card.Title className="card-title">{title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{author}</Card.Subtitle>
                    <Card.Text className="card-text"><small className="text-muted">{desc.slice(
                        0,
                        150
                        ) + '...'}</small></Card.Text>

                    <Card.Link href={book.volumeInfo.previewLink}>Know more</Card.Link>
                                    
                </Card.Body>  
            </Card>  
    </div>     


   
