import React, { useState } from "react";


const Search = (props) => {
  const [searchValue, setSearchValue] = useState("");
  
  const handleSearchInputChanges = (e) => {
    setSearchValue(e.target.value);
    props.inputValue(e.target.value);
  }

  const resetInputField = () => {
    setSearchValue("")
  }

  const callSearchFunction = (e) => {
    e.preventDefault();
    props.search(searchValue);
    
  }

  return (
      <div>
      <form className="search">
        <input className="input_text_search"
          value={searchValue}
          onChange={handleSearchInputChanges}
          type="text"
        />
        <input  className="input_btn_search" onClick={callSearchFunction} type="submit" value="Search" />
      </form>
      <p className="App-search-result">Search for:{searchValue}</p>
    </div>
    );
    
}

export default Search;
