import React, { useState } from "react";


const Search = ({onSearchClick}  ) => {
  

  const [inputuserText,setInputuserText]  = useState('');
  
  const inputChangeHandler = (e) => {
    setInputuserText(e.target.value);
  }



//   const callSearchFunc = (e) => {
//     e.preventDefault();
//     props.onSearchClick(inputuserText);
//   }

  return (
      <div>
      <form className="search">
        <input className="input_text_search"
          value={inputuserText}
          onChange={inputChangeHandler}
          type="text"
        />
        <input  className="input_btn_search" onClick={(e)=> {e.preventDefault(); onSearchClick();}} type="submit" value="Search" />
      </form>
      <p className="App-search-result">Search for:{inputuserText}</p>
    </div>
    );
    
}

export default Search;
