import React , { useState, useEffect } from 'react';

import Header from "./header";
import Book from "./book";
import Search from "./search";
import '../app/app.css';
import styled from 'styled-components';

import { ReactComponent as Logo } from './app/logo.svg';
import star from './app/star.svg';






// const BOOKS_API_URL = "https://www.googleapis.com/books/v1/volumes";



const App = () => {

  const logo = "https://www.google.com/intl/en_ALL/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png"

  const [loading, setLoading] = useState(false);
  const [books, setBooks] = useState([]);
  const [errorMessage, setErrorMessage] = useState(null);

  //   useEffect(() => {
  //   fetch(BOOKS_API_URL)
  //     .then(response => response.json())
  //     .then(jsonResponse => { 
  //       console.log(jsonResponse);
  //       setBooks(jsonResponse.items);
  //       setLoading(false);
  //     });
  // }, []);

    const search = searchValue => {
    setLoading(true);
    setErrorMessage(null);
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchValue}&key=AIzaSyBurBIdD4xVEt-UahTr2inG7N0vT3rqIA0`)
      .then(response => response.json())
      .then(jsonResponse => {
        if (jsonResponse.totalItems !== 0) {
          setBooks(jsonResponse.items);
          setLoading(false);
        } else {
          setErrorMessage(jsonResponse.Error);
          setLoading(false);
        }
      });
    };




    return (
     <div className="App">
      <Header text="Book" />
      <Search search={search}/>

      <div className="books">
        {loading && !errorMessage ? (
         <span>loading...</span>
         ) : errorMessage ? (
          <div className="errorMessage">{errorMessage}</div>
        ) : (
          books.map((book, index) => (
            <Book key={`${index}-${book.volumeInfo.title}`} book={book} />
          ))
        )}
      </div>
    </div>
  );
};


export default App;
