import React , { useState, useEffect } from 'react';

import Header from "./header";
import Book from "./book";
import Search from "./search";
import '../app/app.css';
import styled from 'styled-components';

import { ReactComponent as Logo } from './app/logo.svg';
import star from './app/star.svg';






// const BOOK_API_URL = "https://www.googleapis.com/books/v1/volumes";



const App = () => {
  const [loading, setLoading] = useState(true);
  const [books, setBooks] = useState([]);
  const [errorMessage, setErrorMessage] = useState(null);

  //   useEffect(() => {
  //   fetch(MOVIE_API_URL)
  //     .then(response => response.json())
  //     .then(jsonResponse => { 
  //       console.log(jsonResponse);
  //       setBooks(jsonResponse.items);
  //       setLoading(false);
  //     });
  // }, []);

    const search = searchValue => {
    setLoading(true);
    setErrorMessage(null);
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchValue}&key=AIzaSyCcqANIYReU_QgK_x2LJsS2YFupkvjz1fc`)
      .then(response => response.json())
      .then(jsonResponse => {
        if (jsonResponse.Response === "True") {
          console.log(jsonResponse);
          setBooks(jsonResponse.items);
          setLoading(false);
        } else {
          setErrorMessage(jsonResponse.Error);
          setLoading(false);
        }
      });
  	};


    return (
     <div className="App">
      <Header text="HOOKED" />
      <Search search={search} />
      <p className="App-intro">Sharing a few of our favourite books</p>
      <div className="books">
        {loading && !errorMessage ? (
         <span>loading...</span>
         ) : errorMessage ? (
          <div className="errorMessage">{errorMessage}</div>
        ) : (
          books.map((book, index) => (
            <Book key={`${index}-${book.volumeInfo.title}`} book={book} />
          ))
        )}
      </div>
    </div>
  );
};


export default App;
