import React from "react";
import { Card } from 'react-bootstrap'; 

const DEFAULT_BOOK_IMAGE =
  "./assets/images/bookDefault.png";


const BookDetail = ({ book }) => {
  let item = book.volumeInfo;

  // Author
  let author = item.authors;

  // Title
  let title = item.title;

  // Description
  let desc = item.description;

  if (typeof desc === 'undefined') {
    desc = 'No description available';
  }

  if (typeof author === 'undefined') {
    author = 'No author';
  }
  
  
  return (
    

        <div className="col-sm-2">  
            <Card style={{ 'marginTop': '10px' }}>  

                <Card.Img variant="top" src={book.volumeInfo.imageLinks === undefined ? DEFAULT_BOOK_IMAGE : book.volumeInfo.imageLinks.thumbnail} alt={book.title} />  
                <Card.Body>  
                    <h5 className="card-title">{title}</h5>  
                    <div className="card-content">
                        <p className="title is-6 has-text-primary has-text-centered is-capitalized">${title}</p>
                        <p className="title is-6 has-text-primary has-text-centered is-capitalized">${author}</p>
                        <p className="has-text-black-ter has-text-weight-normal">${desc.slice(
                        0,
                        150
                        ) + '...'}</p>
                    </div>
                </Card.Body>  
            </Card>  
        </div> 
   
      /* <h2>{book.volumeInfo.title}</h2>
      <div>
        <a href={book.volumeInfo.previewLink}>
          <img
            width="200"
            alt={`The Book titled: ${book.volumeInfo.title}`}
            src=
          />          
        </a>

      </div> 
      
      <p>{book.volumeInfo.subtitle}</p>
      <p>{book.volumeInfo.authors}</p>
      <p>{book.volumeInfo.publisher}</p>
      <p>{book.volumeInfo.description}</p>
      <p>{book.volumeInfo.publishedDate}</p> */
    
       

    
  );
};


export default BookDetail;
