import React, { useState , useRef} from "react";


const Search = ({props}  ) => {

    const renders = useRef(0);
    console.log("renders: ", renders.current++);
  

  
  const inputChangeHandler = (e) => {
    setInputuserText(e.target.value);
  }



//   const callSearchFunc = (e) => {
//     e.preventDefault();
//     props.onSearchClick(inputuserText);
//   }

  return (
      <div>
      <form className="search">
        <input className="input_text_search"
          value={props.inputuserText}
          onChange={inputChangeHandler}
          type="text"
        />
        <input  className="input_btn_search" onClick={(e)=> {e.preventDefault(); onSearchClick(inputuserText);}} type="submit" value="Search" />
      </form>
      <p className="App-search-result">Search for:{inputuserText}</p>
    </div>
    );
    
}

export default React.memo(Search);
