import React from "react";

const DEFAULT_PLACEHOLDER_IMAGE =
  "https://m.media-amazon.com/images/M/MV5BMTczNTI2ODUwOF5BMl5BanBnXkFtZTcwMTU0NTIzMw@@._V1_SX300.jpg";


const Book = ({ book }) => {
  const poster =
    book.Poster === "N/A" ? DEFAULT_PLACEHOLDER_IMAGE : book.volumeInfo.imageLinks.thumbnail;
  return (
    <div className="book">
      <h2>{book.volumeInfo.title}</h2>
      <div>
        <img
          width="200"
          alt={`The Book titled: ${book.volumeInfo.title}`}
          src={poster}
        />
      </div>
      <p>({book.Year})</p>
    </div>
  );
};


export default Book;
