import React , { useState } from 'react';
import axios from 'axios';
import Header from "../components/header";
import Book from "../components/book";
import Search from "../components/search";
import '../app/app.css';
import styled from 'styled-components';

import { ReactComponent as Logo } from './app/logo.svg';
import star from './app/star.svg';






// const BOOKS_API_URL = "https://www.googleapis.com/books/v1/volumes";



const App = () => {

  const logo = "https://www.google.com/intl/en_ALL/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png"

  const [isloading, setIsLoading] = useState(false);
  const [books, setBooks] = useState([]);
  const [errorMessage, setErrorMessage] = useState(null);

  const [defaultsearchVal, setdefaultsearchVal] = useState('');



  const searchResult = searchValue => {
    console.log(searchValue);
    setdefaultsearchVal(searchValue);
    setIsLoading(true);
    setErrorMessage(null);
    axios.get(`https://www.googleapis.com/books/v1/volumes?q=${searchValue}&key=AIzaSyBurBIdD4xVEt-UahTr2inG7N0vT3rqIA0`)
     .then(
          (jsonResponse) =>{
              setBooks(jsonResponse.data.items);
              setIsLoading(false);
          }


      )
      .catch (
        (axiosError) =>{
          setErrorMessage(axiosError);
          setIsLoading(false);
        }

      )
    };




    return (
     <div className="App">
      <Header text="Book" />
      <Search inputVal={defaultsearchVal} onSearchClick={searchResult}/>

      <div className="books">
        {isloading && !errorMessage ? (
         <span>loading...</span>
         ) : errorMessage ? (
          <div className="errorMessage">{errorMessage}</div>
        ) : (
          books.map((book, index) => (
            <Book key={`${index}-${book.volumeInfo.title}`} book={book} />
          ))
        )}
      </div>
    </div>
  );
};


export default App;
