import React, { useState} from "react";


const Search = ({ defaultsearchVal , onSearchClick}  ) => {

    
    console.log("renders: Search ");
  

    const [inputuserText,setInputuserText]  = useState(defaultsearchVal);
  
    const inputChangeHandler = (e) => {
        setInputuserText(e.target.value);
    }



//   const callSearchFunc = (e) => {
//     e.preventDefault();
//     props.onSearchClick(inputuserText);
//   }

  return (
      <>
      <form className="form-inline my-2 my-lg-0">
      <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
      <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
      </form>
      {/* <form className="search">
        <input className="input_text_search"
          value={inputuserText}
          onChange={inputChangeHandler}
          type="text"
        />
        <input  className="input_btn_search" onClick={(e)=> {e.preventDefault(); onSearchClick(inputuserText);}}  type="submit" value="Search" />
      </form> */}
      <p className="App-search-result">Search for:{inputuserText}</p>
    </>
    );
    
}

export default React.memo(Search);
